import { useRef, useState } from 'react'
import { images } from '../constants'

function LoginAccount() {
  const width = useRef(window.innerWidth).current
  const height = useRef(window.innerHeight).current
  return (
    <div
      style={{
        backgroundColor: '#141518',
      }}
    >
      <div
        style={{
          display: 'flex',
          height: height * 0.08,
          flexDirection: 'row',
        }}
      >
        <div
          style={{
            marginTop: height * 0.013,
            marginLeft: width * 0.005,
            height: height * 0.065,
            display: 'flex',
            flexDirection: 'row',
          }}
        >
          <img
            src={images.thu}
            style={{
              height: 26,
              marginLeft: 5,
              alignSelf: 'center',
            }}
          />
          <span
            style={{
              marginLeft: 3,
              marginRight: 8,
              fontSize: 14,
              fontWeight: '500',
              alignSelf: 'center',
              color: '#eaeaea',
            }}
          >
            Back to T&C
          </span>
        </div>
      </div>
      <div
        style={{
          marginTop: 10,
          height: height * 0.18,
          width: width,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <img
          src={'https://i.imgur.com/hPbfzZ2.png'}
          alt="Logo image T&C"
          style={{
            height: height * 0.09,
          }}
        />
      </div>
      <div
        style={{
          marginTop: 20,
          height: height * 0.58,
          width: width,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <div
          style={{
            height: height * 0.51,
            width: width * 0.26,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <span
            style={{
              fontSize: 13,
              fontWeight: '400',
              color: '#eaeaea',
              marginTop: 6,
              marginBottom: 6,
            }}
          >
            Sign In with
          </span>

          <div
            style={{
              height: height * 0.09,
              width: width * 0.228,
              backgroundColor: '#2c2f34',
              borderRadius: 6,
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <div
              style={{
                width: width * 0.04,
                height: height * 0.056,
                backgroundColor: 'red',
                marginRight: 8,
                borderRadius: 4,
                display: 'flex',
              }}
            >
              <div
                style={{
                  flex: 1,

                  borderRadius: 4,
                  backgroundColor: 'white',
                }}
              ></div>
            </div>
            <div
              style={{
                width: width * 0.04,
                height: height * 0.056,
                backgroundColor: 'red',
                marginLeft: 8,
                borderRadius: 4,
              }}
            ></div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default LoginAccount
