import './App.css'
import { images, useWindowDimensions } from './constants'

function App() {
  const { height, width } = useWindowDimensions()
  const placeholderStyle = {
    color: '#e1e1eb',
  }
  return (
    <div className="App">
      <h1>Kaiii</h1>
      <div
        style={{
          display: 'flex',
          height: height * 0.09,
          backgroundColor: '#171544',
          flexDirection: 'row',
        }}
      >
        <img
          src={'https://i.imgur.com/hPbfzZ2.png'}
          alt="Logo image T&C"
          style={{
            height: height * 0.09 - 26,
            marginTop: 13,
            marginBottom: 13,
            marginLeft: 10,
            marginRight: 13,
          }}
        />
        <div
          style={{
            display: 'flex',
            flexDirection: 'row',
            height: height * 0.09 - 26,
            width: width * 0.076,
            marginTop: 13,
            marginBottom: 13,
            backgroundColor: '#1bb76e',
            marginLeft: 15,
            borderRadius: 3,
          }}
        >
          <img
            src={images.add_new_image}
            style={{
              height: height * 0.09 - 36,
              marginTop: 5,
              marginBottom: 5,
              marginLeft: 6,
            }}
          />
          <span
            style={{
              fontSize: 13,
              color: '#fff',
              fontWeight: '500',
              alignSelf: 'center',
              marginLeft: 5,
            }}
          >
            Push Image
          </span>
        </div>

        <div
          style={{
            flex: 1,
            height: height * 0.09,
          }}
        >
          <form action="/search">
            <input
              type="text"
              placeholder="Images, #tags, @users oh my!"
              style={{
                alignSelf: 'center',
                height: height * 0.09 - 32,
                width: width * 0.26,
                marginTop: 16,
                marginBottom: 13,
                backgroundColor: '#454469',
                padding: '0px  0px 0px 10px',
                border: '1px solid transparent',
                borderRadius: '3px',
                outline: '0',
                opacity: '0.8',
                fontSize: '16px',
                fontWeight: '500',
                letterSpacing: '0.4px',
                color: '#fff',
                textShadow: 'inherit',
              }}
            />
            <style>{`input::placeholder { color: ${placeholderStyle.color}; padding: ${placeholderStyle.padding}; }`}</style>
            <img
              src={images.search}
              style={{
                height: height * 0.09 - 38.5,
                marginLeft: 6,
                position: 'relative',
                top: '7px',
                right: '35px',
                filter: 'invert(100%)',
              }}
            />
          </form>
        </div>

        <div
          style={{
            width: 250,
            display: 'flex',
            flexDirection: 'row',
          }}
        >
          <div
            style={{
              width: 50,
              backgroundColor: 'blue',
            }}
          ></div>
          <div
            style={{
              width: 50,
              backgroundColor: 'red',
            }}
          ></div>
          <div
            style={{
              backgroundColor: 'yellow',
              flex: 1,
            }}
          ></div>
        </div>
      </div>
    </div>
  )
}

export default App
