import { images, useWindowDimensions } from '../constants'

function PushImage() {
  const { height, width } = useWindowDimensions()
  return (
    <div
      style={{
        overflow: 'hidden',
        height: height,
        width: width,
        backgroundColor: '#17191f',
        justifyContent: 'center',
        alignItems: 'center',
        display: 'flex',
      }}
    >
      <img
        src={'https://i.imgur.com/hPbfzZ2.png'}
        alt="Logo image T&C"
        style={{
          position: 'absolute',
          top: '0px',
          left: '0px',
          height: height * 0.09 - 26,
          marginTop: 13,
          marginBottom: 13,
          marginLeft: 10,
          marginRight: 13,
        }}
      />

      <div
        style={{
          width: width * 0.68,
          height: height * 0.46,
          borderRadius: 5,
          display: 'flex',
          flexDirection: 'row',
        }}
      >
        <div
          style={{
            width: width * 0.34,
            height: height * 0.45,
            borderTopLeftRadius: 5,
            borderBottomLeftRadius: 5,
            backgroundImage: `url(${images.gojobg})`,
            justifyContent: 'center',
            alignItems: 'center',
            display: 'flex',
          }}
        >
          <div
            style={{
              width: width * 0.23,
              height: height * 0.11,
              border: '3px dashed #78a0bc',
              justifyContent: 'center',
              alignItems: 'center',
              display: 'flex',
            }}
          >
            <p
              style={{
                fontWeight: '500',
                color: '#d9e4ec',
                fontSize: 17,
              }}
            >
              Thả hình ảnh vào đây
            </p>
          </div>
        </div>
        <div
          style={{
            width: width * 0.34,
            height: height * 0.45,
            justifyContent: 'center',
            alignItems: 'center',
            display: 'flex',
            backgroundColor: '#3c424b',
            flexDirection: 'column',
            borderTopRightRadius: 5,
            borderBottomRightRadius: 5,
          }}
        >
          <div
            style={{
              height: height * 0.05,
              width: width * 0.23,
              flexDirection: 'row',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <img
              src={images.choseimage}
              style={{
                height: height * 0.09 - 44,
                marginTop: 5,
                marginBottom: 5,
                marginLeft: 6,
                marginRight: 6,
                filter: 'invert(100%)',
              }}
            />
            <span
              style={{
                fontSize: 14,
                marginLeft: 6,
                marginBottom: 3,
                color: '#dfdfdf',
                fontWeight: 'bold',
              }}
            >
              Choose Photo/Video
            </span>
          </div>
          <div
            style={{
              height: height * 0.05,
              width: width * 0.23,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              flexDirection: 'row',
            }}
          >
            <div
              style={{
                height: 1 * 0.09,
                width: 50,
                backgroundColor: '#9a9a9a',
              }}
            ></div>
            <span
              style={{
                fontSize: 14,
                marginLeft: 8,
                marginRight: 8,
                marginBottom: 3,
                color: '#dfdfdf',
                fontWeight: 'bold',
              }}
            >
              or
            </span>
            <div
              style={{
                backgroundColor: '#9a9a9a',
                height: 1 * 0.09,
                width: 50,
              }}
            ></div>
          </div>
          <div
            style={{
              height: height * 0.05,
              backgroundColor: 'yellow',
              width: width * 0.23,
            }}
          ></div>

          <div
            style={{
              marginTop: 15,
              height: height * 0.15,
              backgroundColor: 'white',
              width: width * 0.23,
            }}
          ></div>
        </div>
      </div>
    </div>
  )
}
export default PushImage
