import { useRef } from 'react'
import { images } from '../constants'

function LoginAccount() {
  const width = useRef(window.innerWidth).current
  const height = useRef(window.innerHeight).current

  return (
    <div
      style={{
        flex: 1,
        display: 'flex',
        height: height,
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
      }}
    >
      <span
        style={{
          color: 'black',
          fontSize: 20,
          fontWeight: 'bold',
          marginBottom: 20,
        }}
      >
        Đăng nhập
      </span>
      <div
        style={{
          height: height * 0.142,
          width: width * 0.228,
          backgroundColor: '#2c2f34',
          borderRadius: 6,
          display: 'flex',
          justifyContent: 'space-around',
          alignItems: 'center',
          flexDirection: 'column',
        }}
      >
        <input
          type="text"
          placeholder={'Username or Email'}
          // onFocus={handleFocus1}
          // onBlur={handleBlur1}
          style={{
            marginTop: 10,
            backgroundColor: '#191919',
            color: '#fff',
            border: '1px solid transparent',
            outline: 'none',
            padding: 8,
            width: width * 0.197,
            borderRadius: 5,
            textAlign: 'left',
            justifyContent: 'center',
            paddingLeft: 10,
          }}
        />

        <input
          type="password"
          placeholder={'Password'}
          // onFocus={handleFocus2}
          // onBlur={handleBlur2}
          style={{
            marginBottom: 10,
            backgroundColor: '#191919',
            color: '#fff',
            border: '1px solid transparent',
            outline: 'none',
            padding: 8,
            width: width * 0.197,
            borderRadius: 5,
            textAlign: 'left',
            justifyContent: 'center',
            paddingLeft: 10,
          }}
        />
      </div>
    </div>
  )
}

export default LoginAccount
